import { useEffect, useRef, useState } from 'react'
import { observer } from 'mobx-react-lite'
import canvasState from '../store/canvasState'
import toolState from '../store/toolState'
import Brush from '../tools/Brush'
import '../styles/canvas.scss'
import { Button, Modal } from 'react-bootstrap'
import { useParams } from 'react-router-dom'
import Rect from '../tools/Rect'


const Canvas = observer(() => {
	const canvasRef = useRef()
	const usernameRef = useRef()
	const params = useParams()
	const [isShown, setIsShown] = useState(true)

	const connectionHandler = () => {
		setIsShown(false)
		canvasState.setUserName(usernameRef.current.value)
	}

	useEffect(() => {
		canvasState.setCanvas(canvasRef.current)
	}, [])

	useEffect(() => {
		if (canvasState.username) {
			const socket = new WebSocket('ws://localhost:1511')
			canvasState.setSocket(socket)
			canvasState.setSession(params.id)
			toolState.setTool(new Brush(canvasRef.current, socket, params.id))
			socket.onopen = () => {
				console.log('Подключение успешно')
				socket.send(JSON.stringify({
					id: params.id,
					username: canvasState.username,
					method: 'connection'
				}))
			}
			socket.onmessage = (event) => {
				let msg = JSON.parse(event.data)
				switch (msg.method) {
					case 'connection':
						console.log(`Пользователь ${msg.username} присоединился`)
						break
					case 'draw':
						drawHandler(msg)
						break
				}
				console.log(event.data)
			}
		}
	}, [canvasState.username])

	const drawHandler = (msg) => {
		const figure = msg.figure
		const context = canvasRef.current.getContext('2d')
		switch (figure.type) {
			case 'brush':
				Brush.draw(context, figure.x, figure.y)
				break
			case 'finish':
				context.beginPath()
				break
			case 'rect':
				Rect.staticDraw(context, figure.x, figure.y, figure.width, figure.height)
		}
	}

	const mouseDownHandler = () => {
		canvasState.pushToUndo(canvasRef.current.toDataURL())
	}
	return (
		<div className='canvas'>
			<Modal show={isShown} onHide={() => {
			}}>
				<Modal.Header closeButton>
					<Modal.Title>Введите ваше имя</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<input
						ref={usernameRef}
						type='text'
						style={{ width: '100%', height: '100%', borderColor: 'transparent' }}
					/>
				</Modal.Body>
				<Modal.Footer>
					<Button variant='primary' onClick={() => connectionHandler()}>
						Войти
					</Button>
				</Modal.Footer>
			</Modal>
			<canvas
				onMouseDown={mouseDownHandler}
				ref={canvasRef}
				width={600}
				height={400}
			/>
		</div>
	)
})

export default Canvas
