import toolState from '../store/toolState'
import Brush from '../tools/Brush'
import '../styles/toolbar.scss'
import canvasState from '../store/canvasState'
import Rect from '../tools/Rect'
import Circle from '../tools/Cirlce'

const Toolbar = () => {
	const changeColor = e => {
		toolState.setStrokeColor(e.target.value)
		toolState.setFillColor(e.target.value)
	}
	return (
		<div className='toolbar'>
			<button
				className='toolbar__btn brush'
				onClick={() => toolState.setTool(new Brush(canvasState.canvas, canvasState.socket, canvasState.session))}
			/>
			<button
				className='toolbar__btn rect'
				onClick={() => toolState.setTool(new Rect(canvasState.canvas, canvasState.socket, canvasState.session))}
			></button>
			, canvasState.socket, canvasState.session
			<button
				className='toolbar__btn circle'
				onClick={() => toolState.setTool(new Circle(canvasState.canvas))}
			></button>
			<button
				className='toolbar__btn eraser'
				onClick={() => {
					toolState.setTool(new Brush(canvasState.canvas, canvasState.socket))
					toolState.setStrokeColor('#FFFFFF')
					toolState.setFillColor('#FFFFFF')
				}}
			></button>
			<button className='toolbar__btn line'></button>
			<input
				type='color'
				onChange={e => changeColor(e)}
				style={{ marginLeft: '10px' }}
			/>
			<button
				className='toolbar__btn undo'
				onClick={() => canvasState.undo()}
			></button>
			<button
				className='toolbar__btn redo'
				onClick={() => canvasState.redo()}
			></button>
			<button className='toolbar__btn save'></button>
		</div>
	)
}

export default Toolbar
